mapper.select:
select 

from GP_STOCK_CLASS_MAP
where STOCK_CODE = #{stockCode,jdbcType=VARCHAR}
  and CLASSIFY_CODE = #{classifyCode,jdbcType=VARCHAR}

select 

from GP_STOCK_CLASS_MAP
where DELETED_FLAG='0' and CLASSIFY_CODE = #{classifyCode,jdbcType=VARCHAR}

mapper.delete:
delete from GP_STOCK_CLASS_MAP
where STOCK_CODE = #{stockCode,jdbcType=VARCHAR}
  and CLASSIFY_CODE = #{classifyCode,jdbcType=VARCHAR}

mapper.update:
update GP_STOCK_CLASS_MAP
set WEIGHT = #{weight,jdbcType=DECIMAL},
  INCLU_DATE = #{incluDate,jdbcType=DATE},
  DELETED_FLAG = #{deletedFlag,jdbcType=CHAR},
  ORIGIN_CODE = #{originCode,jdbcType=VARCHAR},
  CREATED_DATE = #{createdDate,jdbcType=TIMESTAMP},
  LAST_UPD_DATE = #{lastUpdDate,jdbcType=TIMESTAMP},
  MODIFICATION_NUM = #{modificationNum,jdbcType=DECIMAL}
where STOCK_CODE = #{stockCode,jdbcType=VARCHAR}
  and CLASSIFY_CODE = #{classifyCode,jdbcType=VARCHAR}
