mapper.select:
select 

from GP_STOCK_KLINE_30F
where STOCK_CODE = #{stockCode,jdbcType=VARCHAR}
  and PERIOD_CODE = #{periodCode,jdbcType=VARCHAR}

select 

from GP_STOCK_KLINE_30F
where DELETED_FLAG='0' and STOCK_CODE = #{stockCode,jdbcType=VARCHAR} 
AND SEQUENCE_NO = (SELECT MAX(SEQUENCE_NO) 
                        FROM GP_STOCK_KLINE_30F
                        WHERE DELETED_FLAG='0' AND ORIGIN_CODE='H'
                        AND STOCK_CODE = #{stockCode,jdbcType=VARCHAR}
                   )

mapper.delete:
delete from GP_STOCK_KLINE_30F
where STOCK_CODE = #{stockCode,jdbcType=VARCHAR}
  and PERIOD_CODE = #{periodCode,jdbcType=VARCHAR}
